$(document).ready(function () {


	// call mobile mnu
	$('.callMenu').click(function(){
		$('.mobileMenu').slideToggle(300);
		$('.burgerButton').toggleClass('active');
	});


	// scroll and call
	$('.scrollToMnu').click(function(){
		$('body,html').animate({scrollTop: 0 }, 1000);
		$('.mobileMenu').slideToggle(300);
		$('.burgerButton').toggleClass('active');
	});


	// scroll to ancher
	$(".nav").on("click","a", function (event) {
		event.preventDefault();
		var id  = $(this).attr('href'),
			top = $(id).offset().top,
			pad = 50;
		if (id === '#main') {
			pad = 0;
		}
		$('body,html').animate({scrollTop: top - pad}, 1000);
		if($(window).width() < 1200) {
			$('.nav').slideToggle(300);
			$('.burgerButton').toggleClass('active');
		}
	});


	// reviews slider
	var reviewsSlider = $('.reviewsSlider');
	function initSlider() {
		if($(window).width() < 768) {
			reviewsSlider.owlCarousel({
				items: 1,
			}).addClass('owl-carousel');
		} else {
			reviewsSlider.removeClass('owl-carousel');
			reviewsSlider.trigger('destroy.owl.carousel');
		}
	}
	$(window).on('resize load', initSlider)


});